//Settings
class config{
  constructor(fps,color1,color2,color3,color4,color5,color6,bs,tag1,tag2,tag3,tag4,width,width2) 
  {
    this.fps=fps;
    this.color1 = color1;
    this.color2 = color2;
    this.color3 = color3;
    this.color4 = color4;
    this.color5 = color5;
    this.color6 = color6;
    this.brushSize = bs;
    this.tag1 = tag1;
    this.tag2 = tag2;
    this.tag3 = tag3;
    this.tag4 = tag4;
    this.width = width;
    this.width2 = width2;
  }
}

//corrections related variables
class correction{
  constructor(categ,img,txt,isFixed) 
  {
    this.category=categ;
    this.visData=img;
    this.txtData=txt;
    this.corrected = isFixed;
  }
}

